mapper.insert:
INSERT INTO pusers
(
	id
	, pwd
	, name
	, phone
	, addr
	, email

		,naveremail
		,uniqid

	, regdate
)
VALUES
(
	#{id}
	, #{pwd}
	, #{name}
	, #{phone}
	, #{addr}
	, #{email}

		,#{naveremail}
		,#{uniqid}

	, SYSDATE)

mapper.update:
UPDATE pusers
SET phone = #{phone}, email = #{email}, addr = #{addr}
WHERE id = #{id}

UPDATE pusers
SET pwd = #{pwd}
WHERE id = #{id}

UPDATE pusers
SET pwd = #{pwd}
WHERE email = #{email}
